mapper.select:
SELECT * FROM ( SELECT row_number() over (ORDER BY system_create_date DESC )AS no,
bbs_id, bbs_nm, bbs_type, read_auth, write_auth, modify_auth, delete_auth, reply_auth, file_yn, reply_yn, system_create_date, system_update_date FROM SYS_BBS_MST
AND bbs_id = #{bbs_id} AND bbs_nm = #{bbs_nm} AND bbs_type = #{bbs_type} AND read_auth = #{read_auth} AND write_auth = #{write_auth} AND modify_auth = #{modify_auth} AND delete_auth = #{delete_auth} AND reply_auth = #{reply_auth} AND file_yn = #{file_yn} AND reply_yn = #{reply_yn} AND system_create_date = #{system_create_date} AND system_update_date = #{system_update_date}
AND bbs_id LIKE CONCAT('%', #{_bbs_id}, '%') AND bbs_nm LIKE CONCAT('%', #{_bbs_nm}, '%') AND bbs_type LIKE CONCAT('%', #{_bbs_type}, '%') AND read_auth LIKE CONCAT('%', #{_read_auth}, '%') AND write_auth LIKE CONCAT('%', #{_write_auth}, '%') AND modify_auth LIKE CONCAT('%', #{_modify_auth}, '%') AND delete_auth LIKE CONCAT('%', #{_delete_auth}, '%') AND reply_auth LIKE CONCAT('%', #{_reply_auth}, '%') AND file_yn LIKE CONCAT('%', #{_file_yn}, '%') AND reply_yn LIKE CONCAT('%', #{_reply_yn}, '%') AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%') AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d') AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)
AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d') AND system_create_date < DATE_ADD(STR_TO_DATE(#{from_date}, '%Y%m%d'), INTERVAL 1 DAY)
AND system_create_date >= STR_TO_DATE(#{to_date}, '%Y%m%d') AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)
) AS sub
LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_BBS_MST


    AND bbs_id = #{bbs_id}
    AND bbs_nm = #{bbs_nm}
    AND bbs_type = #{bbs_type}
    AND read_auth = #{read_auth}
    AND write_auth = #{write_auth}
    AND modify_auth = #{modify_auth}
    AND delete_auth = #{delete_auth}
    AND reply_auth = #{reply_auth}
    AND file_yn = #{file_yn}
    AND reply_yn = #{reply_yn}
    AND system_create_date = #{system_create_date}
    AND system_update_date = #{system_update_date}

    AND bbs_id LIKE CONCAT('%', #{_bbs_id}, '%')
    AND bbs_nm LIKE CONCAT('%', #{_bbs_nm}, '%')
    AND bbs_type LIKE CONCAT('%', #{_bbs_type}, '%')
    AND read_auth LIKE CONCAT('%', #{_read_auth}, '%')
    AND write_auth LIKE CONCAT('%', #{_write_auth}, '%')
    AND modify_auth LIKE CONCAT('%', #{_modify_auth}, '%')
    AND delete_auth LIKE CONCAT('%', #{_delete_auth}, '%')
    AND reply_auth LIKE CONCAT('%', #{_reply_auth}, '%')
    AND file_yn LIKE CONCAT('%', #{_file_yn}, '%')
    AND reply_yn LIKE CONCAT('%', #{_reply_yn}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')

        AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d')
        AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)



        AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d')
        AND system_create_date < DATE_ADD(STR_TO_DATE(#{from_date}, '%Y%m%d'), INTERVAL 1 DAY)



        AND system_create_date >= STR_TO_DATE(#{to_date}, '%Y%m%d')
        AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)

SELECT

    bbs_id,
    bbs_nm,
    bbs_type,
    read_auth,
    write_auth,
    modify_auth,
    delete_auth,
    reply_auth,
    file_yn,
    reply_yn,
    system_create_date,
    system_update_date
FROM SYS_BBS_MST


    AND bbs_id = #{bbs_id}
    AND bbs_nm = #{bbs_nm}
    AND bbs_type = #{bbs_type}
    AND read_auth = #{read_auth}
    AND write_auth = #{write_auth}
    AND modify_auth = #{modify_auth}
    AND delete_auth = #{delete_auth}
    AND reply_auth = #{reply_auth}
    AND file_yn = #{file_yn}
    AND reply_yn = #{reply_yn}
    AND system_create_date = #{system_create_date}
    AND system_update_date = #{system_update_date}

    AND bbs_id LIKE CONCAT('%', #{_bbs_id}, '%')
    AND bbs_nm LIKE CONCAT('%', #{_bbs_nm}, '%')
    AND bbs_type LIKE CONCAT('%', #{_bbs_type}, '%')
    AND read_auth LIKE CONCAT('%', #{_read_auth}, '%')
    AND write_auth LIKE CONCAT('%', #{_write_auth}, '%')
    AND modify_auth LIKE CONCAT('%', #{_modify_auth}, '%')
    AND delete_auth LIKE CONCAT('%', #{_delete_auth}, '%')
    AND reply_auth LIKE CONCAT('%', #{_reply_auth}, '%')
    AND file_yn LIKE CONCAT('%', #{_file_yn}, '%')
    AND reply_yn LIKE CONCAT('%', #{_reply_yn}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')

mapper.insert:
